import React from "react";
import { useState, useEffect } from "react";
import "./components/sortingVisualizer/index.css";

export default function SortingVisualizer() {
  const [state, setState] = {
    array: [],
  };

  //Once component mounts reset the array;
  useEffect(() => {
    resetArray();
  });

  //Function that generates a new array
  function resetArray() {
    const array = [];
    for (let i = 0; i <= 100; i++) {
      array.push(Math.floor(Math.random() * (1000 - 5 + 1) + 5));
    }
    setState({ array });
  }
  state.days.map((value, index) => {
    return <div key={index}>{value}</div>;
  });
}
