import React from "react";
import { useState, useEffect } from "react";
import "./index.css";

export default function SortingVisualizer() {
  const [state, setState] = useState({
    array: []
  });

  //Once component mounts reset the array;
  useEffect(() => {
    resetArray();
  }, []);

  //Function that generates a new array
  function resetArray() {
    let array = [];
    for (let i = 0; i <= 290; i++) {
      array.push(Math.floor(Math.random() * (900 - 5 + 1) + 5));
    }
    setState({ array });
  }

  return (
      <div className="array-container">
        {state.array.map((value, index) => 
          <div 
            className="array-bar" 
            key={index}
            style={{height: `${value}px`}}></div>
        )}
        <button onClick={()=> resetArray()}>Generate New Array</button>
        <button onClick={()=> resetArray()}>Merge Sort</button>
        <button onClick={()=> resetArray()}>Bubble Sort</button>
        <button onClick={()=> resetArray()}>Heap Sort</button>
        <button onClick={()=> resetArray()}>Selection Sort</button>
      </div>
  )
}
